pragma solidity ^0.4.18;

contract LMS{
    /*
    * Common Functions 
    */
    address[] myarray;
    function isAddressAvailable(address commonAdress) public constant returns(bool isAvail){
        if(myarray.length == 0){
            return false;
        } else {
            return ((myarray[Students[commonAdress].student_id] == commonAdress) 
                || (myarray[Books[commonAdress].book_no] == commonAdress)
                || (myarray[Librarians[commonAdress].lib_id] == commonAdress)
                );
        }    
    }
    
    function checkIsLibrarian(address isLib) private view returns (bool bValue){
        if(librariansIndex.length == 0) return false;
        return (librariansIndex[Librarians[isLib].lib_id] == msg.sender);
    }
    
    /*
    * Librarian Master Details
    */
    struct Librarian{
        uint lib_id;
        string lib_name;
    }
    
   
    mapping (address => Librarian) private Librarians;
    address[] private librariansIndex;
    event LogNewLibrarian(address libData, string lib_name);
    
    function insertLibrarian(address lib_address, string librarian_name) public returns (uint lib_id){
        if(isAddressAvailable(lib_address)){
            emit errorLog("Please select different address...");
        } else{ 
            Librarians[lib_address].lib_name = librarian_name;
            librariansIndex.push(lib_address)-1;
            myarray.push(lib_address) - 1;
            Librarians[lib_address].lib_id = librariansIndex.length-1;
            emit LogNewLibrarian(lib_address, librarian_name);
            return librariansIndex.length-1;
        }
    }
    
    function getLibrarianCount() private constant returns(uint count)
    {
        return librariansIndex.length;
    }
    
    function getLibrarianData(address libAddress) public view returns(uint lib_id, string lib_name){
        Librarian memory l = Librarians[libAddress];
        return (l.lib_id, l.lib_name);
    }
    
    /*
    * Student Master Details
    */
    struct Student{
         uint student_id;
         string student_roll_no;
         string student_name;
         string student_class;
         string student_postal_address;
         mapping (address => bookIssueStudent) bookIssue;
         mapping (address => recordLogBookIssue) recordLogIssue;
    }
    mapping (address => Student) private Students;
    address[] private studentIndex;
    event LogNewStudent(address studentData, string roll_no, string st_name, string class);
    event errorLog(string errorText);
    event errorLog1(bool errorText);
    event errorLog2(uint errorVal);
    
    function insertStudent(address studentAddress, string student_roll_no, string student_name, string student_class, string student_postal_address) public returns(uint stud_id)
    {
        if(!checkIsLibrarian(studentAddress)){
            emit errorLog("Not authorized to add student..");
        }
        else if(isAddressAvailable(studentAddress)){
            emit errorLog("Address already Exists..");
        }
        else{
            Students[studentAddress].student_roll_no = student_roll_no;
            Students[studentAddress].student_name = student_name;
            Students[studentAddress].student_class = student_class;
            Students[studentAddress].student_postal_address = student_postal_address;
            studentIndex.push(studentAddress)-1;
            myarray.push(studentAddress) - 1;
            Students[studentAddress].student_id = studentIndex.length-1;
            emit LogNewStudent(studentAddress, student_roll_no, student_name,student_class);
            return studentIndex.length-1;    
        } 
    }
    
    function getStudentCount() public constant returns(uint count)
    {
        return studentIndex.length;
    }
    
    function getStudentData(address studentAddress) public view returns(uint id, string student_roll_no, string student_name, string student_class, string student_postal_address){
        Student memory s = Students[studentAddress];
        return (s.student_id, s.student_roll_no, s.student_name, s.student_class, s.student_postal_address);
    }
    /*
    * Book Master Details
    */
    struct Book{
        uint book_no;
        string book_category;
        string book_name;
        string book_author;
        string book_publisher;
        string book_edition;
        uint book_no_of_copies;
    }
    mapping (address => Book) Books;
    address[] private bookIndex;
    event LogNewBook(address book_add, string book_category, string book_name, string book_author, string book_publisher, string book_edition, uint book_no_of_copies);
    
    function insertBook(address bookAddress, string book_category, string book_name, string book_author, string book_publisher, string book_edition, uint book_no_of_copies) public returns(uint bookNo) {
         if(!checkIsLibrarian(bookAddress)){
            emit errorLog("Not authorized to add book..");
        } else if(isAddressAvailable(bookAddress)){
            emit errorLog("Address already Exists..");
        }
        else{
            if(keccak256(b_Categories[book_category].category_name) == keccak256(book_category)){
                Books[bookAddress].book_category = book_category;
                Books[bookAddress].book_name = book_name;
                Books[bookAddress].book_author = book_author;
                Books[bookAddress].book_publisher = book_publisher;
                Books[bookAddress].book_edition = book_edition;
                Books[bookAddress].book_no_of_copies = book_no_of_copies;
                bookIndex.push(bookAddress)-1;
                myarray.push(bookAddress) - 1;
                Books[bookAddress].book_no = bookIndex.length - 1;
                emit LogNewBook(bookAddress, book_category, book_name, book_author, book_publisher, book_edition, book_no_of_copies);
                return bookIndex.length-1;     
            }else {
                emit errorLog("Please insert appropriate category from master.");
            }
               
        } 
    }
    
    function getBooksCount() public constant returns(uint count)
    {
        return bookIndex.length;
    }
    
    function getBookData(address bookAddress) public view returns(uint book_no, string book_category, string book_name, string book_author, string book_publisher, string book_edition, uint book_no_of_copies){
        Book memory bk = Books[bookAddress];
        return (bk.book_no, bk.book_category, bk.book_name, bk.book_author, bk.book_publisher, bk.book_edition, bk.book_no_of_copies);
    }
    
    /*
    * Books category Details
    */
    struct b_Category{
        uint category_id;
        string category_name;
    }
    mapping (string => b_Category) b_Categories;
    string[] b_Category_Index;
    
    function insertBookCategory(uint category_id, string book_cat_name) public {
        b_Categories[book_cat_name].category_id = category_id;
        b_Categories[book_cat_name].category_name = book_cat_name;
    }

    /*
    * Book Issue to Student Details
    */
    
    struct bookIssueStudent{
        address book_no;
        uint book_issue_date;
        uint book_return_date;
        bool isAvailibility;
    }
    
    struct recordLogBookIssue{
        address book_no;
        uint book_issue_date;
        uint book_return_date;
        string appendIssuedate;
        string appendReturndate;
        uint count;
    }
    address[] public recordLogIndex;

    function bookIssueReturn(address book_ref, address student_ref, string options) public{
            bookIssuetoStudent(book_ref,student_ref, options );
    }
    
    function bookIssuetoStudent(address book_ref, address student_ref, string options) private {
        if(checkIsLibrarian(msg.sender)){
            if(studentIndex.length != 0 && studentIndex[Students[student_ref].student_id] == student_ref){
                if(bookIndex.length != 0 && Books[book_ref].book_no_of_copies > 0){
                    if(keccak256(options) == keccak256("issue")){
                        if((Students[student_ref].bookIssue[book_ref].book_no == book_ref) && (Students[student_ref].bookIssue[book_ref].isAvailibility == true)){
                               emit errorLog("This book already Exists.");
                        }else{
                            //Students[student_ref].bookIssue[book_ref] = bookIssueStudent(book_ref,now,0, 1);
                            Students[student_ref].bookIssue[book_ref].book_no = book_ref;
                            Students[student_ref].bookIssue[book_ref].book_issue_date = now;
                            Students[student_ref].bookIssue[book_ref].book_return_date = 0;
                            Students[student_ref].bookIssue[book_ref].isAvailibility = true;
                            Books[book_ref].book_no_of_copies -= 1;
                        }     
                        emit errorLog1(Students[student_ref].bookIssue[book_ref].isAvailibility);
                    } else if (keccak256(options) == keccak256("return")){
                        if(Students[student_ref].bookIssue[book_ref].book_no == book_ref && Students[student_ref].bookIssue[book_ref].isAvailibility == true){
                            Students[student_ref].bookIssue[book_ref].book_return_date = now;
                            Books[book_ref].book_no_of_copies += 1;                           
                            if(recordLogIndex.length == 0){
                                emit errorLog("HEre1");
                                Students[student_ref].recordLogIssue[book_ref].count = 1;
                                Students[student_ref].recordLogIssue[book_ref].book_issue_date = Students[student_ref].bookIssue[book_ref].book_issue_date;
                                Students[student_ref].recordLogIssue[book_ref].book_return_date = Students[student_ref].bookIssue[book_ref].book_return_date;
                                Students[student_ref].bookIssue[book_ref].isAvailibility = false;
                                emit errorLog1(Students[student_ref].bookIssue[book_ref].isAvailibility);
                                emit errorLog(Students[student_ref].recordLogIssue[book_ref].appendIssuedate);
                            } else {
                                emit errorLog("HEre2");
                                Students[student_ref].recordLogIssue[book_ref].count += 1;
                                //string memory old_issue_date = uint2str(Students[student_ref].recordLogIssue[book_ref].book_issue_date);
                                //string memory new_issue_date = uint2str(Students[student_ref].bookIssue[book_ref].book_issue_date);  
                                //Students[student_ref].recordLogIssue[book_ref].appendIssuedate = concat(old_issue_date,new_issue_date);                                
                                Students[student_ref].bookIssue[book_ref].isAvailibility = false;
                                emit errorLog(Students[student_ref].recordLogIssue[book_ref].appendIssuedate);
                            }
                        } else {
                            emit errorLog("This student is not belowng to this book.");
                        }
                    }
                    else{
                        emit errorLog("Please enter proper option");
                    }
                }
                else{
                    emit errorLog("Book is Not Available!");
                }
            } else {
                 emit errorLog("This Member ID is not registerd!");
            }
        } else {
            emit errorLog("Not authorized to Issue book to student..");
        }
        //student Exists
        // book copies avaialble
    }
    
